mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rnum, A.* FROM ( SELECT CASE CODE_TYPE WHEN '01' THEN '자유' WHEN '02' THEN '익명' WHEN '03' THEN 'QnA' ELSE '기타' END AS codeType, NUM, NAME, TITLE, TO_CHAR(REGDATE, 'YYYY/MM/DD') AS regdate, BHIT FROM FREEBOARD
CODE_TYPE = #{codeType}
TITLE LIKE '%' ||#{title}|| '%'
NAME LIKE '%'||#{name}||'%'
NUM = #{num}
TO_CHAR(REGDATE , 'YYYYMMDD') BETWEEN #{startDate} AND #{endDate}
ORDER BY NUM ASC ) A WHERE ROWNUM <= #{endPage} ) WHERE rnum >= #{startPage}

      SELECT
		COUNT(*)
      FROM FREEBOARD


          CODE_TYPE = #{codeType}


          TITLE LIKE '%' ||#{title}|| '%' 


          NAME LIKE '%'||#{name}||'%' 


          NUM = #{num}


          TO_CHAR(REGDATE , 'YYYYMMDD') BETWEEN #{startDate} AND #{endDate}


      ORDER BY NUM ASC
